public class LineDrawingUsingArrowKeyws{  static final double WIDTH = 400;  static final double HEIGHT = 250;  double x;  double y;  @Override  public void start(Stage primaryStage) throws Exception {    x = WIDTH / 2;     y = HEIGHT / 2;     Pane pane = new Pane();    Canvas canvas = new Canvas(WIDTH, HEIGHT);    GraphicsContext gc = canvas.getGraphicsContext2D();    gc.moveTo(x, y);    gc.setFill(Color.BLACK);    gc.setLineWidth(3);    pane.getChildren().add(canvas);    Scene scene = new Scene(pane, WIDTH, HEIGHT);    scene.setOnKeyPressed(e -> {      KeyCode keyCode = e.getCode();      double addX = 0, addY = 0;      if (keyCode == KeyCode.UP) {        addY -= 2;      } else if (keyCode == KeyCode.DOWN) {        addY += 2;      } else if (keyCode == KeyCode.LEFT) {        addX -= 2;      } else if (keyCode == KeyCode.RIGHT) {        addX += 2;      }      gc.lineTo(x + addX, y + addY);      x += addX;      y += addY;      gc.stroke();    });    primaryStage.setTitle(getClass().getName());    primaryStage.setScene(scene);    primaryStage.show();  }  public static void main(String[] args) {    launch(args);  }}