public class ClockExample {  private double WIDTH = 450;  private double HEIGHT = 400;  SimpleIntegerProperty hourProperty = new SimpleIntegerProperty(12);  SimpleIntegerProperty minuteProperty = new SimpleIntegerProperty(0);  SimpleIntegerProperty secondProperty = new SimpleIntegerProperty(0);  @Override  public void start(Stage primaryStage) {    VBox mainLayoutBox = new VBox(5);    HBox inputsBox = new HBox(1);    ClockPane clockPane = new ClockPane();    Label hourLabel = new Label("Hour");    Label minutelabel = new Label("Minute");    Label secondsLabel = new Label("Second");    TextField hourInput = new TextField();    TextField minuteInput = new TextField();    TextField secondInput = new TextField();    hourInput.textProperty().bindBidirectional(hourProperty, new NumberStringConverter());    minuteInput.textProperty().bindBidirectional(minuteProperty, new NumberStringConverter());    secondInput.textProperty().bindBidirectional(secondProperty, new NumberStringConverter());    inputsBox.getChildren().add(hourLabel);    inputsBox.getChildren().add(hourInput);    inputsBox.getChildren().add(minutelabel);    inputsBox.getChildren().add(minuteInput);    inputsBox.getChildren().add(secondsLabel);    inputsBox.getChildren().add(secondInput);        mainLayoutBox.getChildren().add(clockPane);    VBox.setVgrow(clockPane, Priority.ALWAYS);        mainLayoutBox.getChildren().add(inputsBox);    mainLayoutBox.widthProperty().addListener((observable, oldValue, newValue) -> {      hourInput.setMaxWidth(newValue.doubleValue() / 6);       secondInput.setMaxWidth(newValue.doubleValue() / 6);      minuteInput.setMaxWidth(newValue.doubleValue() / 6);      clockPane.setW(newValue.doubleValue());    });    mainLayoutBox.heightProperty().addListener((observable, oldValue, newValue) -> {      Number inputFooterHeight = inputsBox.getHeight();      clockPane.setH(newValue.doubleValue() - inputFooterHeight.doubleValue());    });    clockPane.setHour(hourProperty.intValue());    clockPane.setMinute(minuteProperty.intValue());    clockPane.setSecond(secondProperty.intValue());    hourProperty.addListener((o, ov, nv) -> {      clockPane.setHour(nv.intValue());    });    minuteProperty.addListener((o, ov, nv) -> {      clockPane.setMinute(nv.intValue());    });    secondProperty.addListener((o, ov, nv) -> {      clockPane.setSecond(nv.intValue());    });    Scene scene = new Scene(mainLayoutBox, WIDTH, HEIGHT);    primaryStage.setTitle(getClass().getName());    primaryStage.setScene(scene);    primaryStage.setResizable(false);    primaryStage.show();  }}